import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
@Component({
  selector: 'app-join-form',
  templateUrl: './join-form.component.html',
  styleUrls: ['./join-form.component.css'],
})
export class JoinFormComponent {
  user = new FormGroup({
    name: new FormControl(''),
    url: new FormControl(''),
  });

  ngOnInit() {}
  submitJoinForm() {
    console.log(this.user.value);
  }
}
<form [formGroup]="user" (ngSubmit)="submitJoinForm()">
  <label for="name">Name</label>
  <input type="text" id="name" formControlName="name" />
  <br />
  <label for="url">Daily room URL</label>
  <input type="text" id="url" formControlName="url" />
  <br />
  <input type="submit" />
</form>

<p>name: {{ user.get('name').value }}</p>
<p>url: {{ user.value }}</p>
